const pool = require('../config/db'); // สมมติคุณสร้าง pool ไว้ที่นี่

const getBorrowHistoryWithDevice = async (req, res) => {
  try {
    const sql = `
      SELECT
        d.type,
        d.name AS device_name,
        d.brand,
        d.id_code,
        d.serial_number,
        bh.borrower_name,
        bh.department,
        bh.reason,
        bh.borrowed_at,
        bh.returned_at
      FROM devices d
      LEFT JOIN borrow_history bh ON bh.device_id = d.id
      ORDER BY bh.borrowed_at DESC
    `;

    const [rows] = await pool.query(sql);
    res.json(rows);
  } catch (error) {
    console.error('Error in getBorrowHistoryWithDevice:', error);
    res.status(500).json({ message: 'Internal Server Error' });
  }
};

module.exports = {
  getBorrowHistoryWithDevice,
};
