import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

export default function Login() {
  const navigate = useNavigate();

  const handleLogin = () => {
    // à¸•à¸£à¸§à¸ˆà¸ªà¸­à¸š username/password à¸«à¸£à¸·à¸­ fetch API
    navigate('/dashboard'); // à¹€à¸›à¸¥à¸µà¹ˆà¸¢à¸™ path à¸«à¸¥à¸±à¸‡ Login à¸ªà¸³à¹€à¸£à¹‡à¸ˆ
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <img src="/logo.png" alt="logo" className="logo" />
        <h2>à¹€à¸‚à¹‰à¸²à¸ªà¸¹à¹ˆà¸£à¸°à¸šà¸š</h2>

        <label>Username</label>
        <input type="text" placeholder="Enter username" />

        <label>Password</label>
        <input type="password" placeholder="Enter password" />

        <button onClick={handleLogin} className="login-btn">Log in</button>
        <button onClick={() => navigate('/register')} className="register-btn">Register</button>
      </div>
    </div>
  );
}
